public class StringConcat {
  static String joinStrings(String a, String b) {
    return a + b;
  }
  public static void main(String[] args) {
    System.out.println(joinStrings("Hello, ", "World!"));
  }
}

public class VowelCounter {
  static int countVowels(String str) {
    int count = 0;
    for (char ch : str.toLowerCase().toCharArray()) {
      if ("aeiou".indexOf(ch) != -1) {
        count++;
      }
    }
    return count;
  }
  public static void main(String[] args) {
    System.out.println(countVowels("Education"));
  }
}

public class PalindromeCheck {
  static boolean isPalindrome(String str) {
    String reversed = new StringBuilder(str).reverse().toString();
    return str.equalsIgnoreCase(reversed);
  }
  public static void main(String[] args) {
    System.out.println(isPalindrome("Madam"));
  }
}

public class ToUpper {
  static String toUpper(String str) {
    return str.toUpperCase();
  }
  public static void main(String[] args) {
    System.out.println(toUpper("hello"));
  }
}

public class ToLower {
  static String toLower(String str) {
    return str.toLowerCase();
  }
  public static void main(String[] args) {
    System.out.println(toLower("HELLO"));
  }
}

public class WordCounter {
  static int countWords(String sentence) {
    String[] words = sentence.trim().split("\\s+");
    return words.length;
  }
  public static void main(String[] args) {
    System.out.println(countWords("Java is simple"));
  }
}

public class SubstringCheck {
  static boolean containsSubstring(String mainStr, String subStr) {
    return mainStr.contains(subStr);
  }
  public static void main(String[] args) {
    System.out.println(containsSubstring("Hello World", "World"));
  }
}

public class StringReverse {
  static String reverse(String str) {
    return new StringBuilder(str).reverse().toString();
  }
  public static void main(String[] args) {
    System.out.println(reverse("OpenAI"));
  }
}

public class ReplaceSpaces {
  static String replaceSpaces(String str) {
    return str.replace(" ", "-");
  }
  public static void main(String[] args) {
    System.out.println(replaceSpaces("Java is fun"));
  }
}

public class CharFrequency {
  static int frequency(String str, char ch) {
    int count = 0;
    for (char c : str.toCharArray()) {
      if (c == ch) count++;
    }
    return count;
  }
  public static void main(String[] args) {
    System.out.println(frequency("banana", 'a'));
  }
}
